mapper.select:
        SELECT


--         级联表的字段

        FROM yisheng  a



                and a.id in

                #{item}



                and a.username like CONCAT('%',#{params.username},'%')


                and a.password like CONCAT('%',#{params.password},'%')


                and a.yisheng_uuid_number like CONCAT('%',#{params.yishengUuidNumber},'%')


                and a.yisheng_name like CONCAT('%',#{params.yishengName},'%')


                and a.yisheng_phone like CONCAT('%',#{params.yishengPhone},'%')


                and a.yisheng_id_number like CONCAT('%',#{params.yishengIdNumber},'%')


                and a.yisheng_shanchang like CONCAT('%',#{params.yishengShanchang},'%')


                and a.sex_types = #{params.sexTypes}


                and a.keshi_types = #{params.keshiTypes}


                and a.zhiwei_types = #{params.zhiweiTypes}


                  and a.guahaofei >= #{params.guahaofeiStart}   


                  and a.guahaofei <= #{params.guahaofeiEnd}   


                  and a.zan_number >= #{params.zanNumberStart}   


                  and a.zan_number <= #{params.zanNumberEnd}   


                and a.zan_number = #{params.zanNumber}


                  and a.cai_number >= #{params.caiNumberStart}   


                  and a.cai_number <= #{params.caiNumberEnd}   


                and a.cai_number = #{params.caiNumber}


                and a.yisheng_content like CONCAT('%',#{params.yishengContent},'%')




        order by a.${params.orderBy} desc
